@push('scripts')
<style>
/* Prevent text selection during touch/swipe on all elements */
* {
  -webkit-user-select: none !important;
  -moz-user-select: none !important;
  -ms-user-select: none !important;
  user-select: none !important;
  -webkit-tap-highlight-color: transparent !important;
  -webkit-touch-callout: none !important;
}

svg, svg * {
  -webkit-user-select: none !important;
  -moz-user-select: none !important;
  user-select: none !important;
  touch-action: none !important;
}

.map-container {
  touch-action: pan-x pan-y !important;
}
</style>
<script>
const panel = document.getElementById('infoPanel');
const panelTitle = document.getElementById('panelTitle');
const panelBody = document.getElementById('panelBody');
const closeBtn = document.getElementById('closePanel');
const mapContainer = document.querySelector('.map-container');
let selectedElement = null;

document.querySelectorAll('svg [id]').forEach(el => {
  el.addEventListener('click', async () => {
    const id = el.getAttribute('id');
    console.log('Clicked building:', id);

    if (selectedElement) {
      selectedElement.style.filter = '';
    }

    el.style.filter = 'brightness(1.3) drop-shadow(0 0 8px rgba(0, 255, 0, 0.8))';
    selectedElement = el;

    try {
      const res = await fetch(`/Navi/public/building-info/${id}`);
      console.log('Response status:', res.status);
      
      if (!res.ok) throw new Error('Not found');
      const data = await res.json();
      console.log('Data received:', data);

      panelTitle.textContent = data.name;
      const officesHtml = data.offices && data.offices.length > 0 
        ? `<h3>Offices:</h3><ul>${data.offices.map(o => `<li><strong>${o.name}</strong>: ${o.services}</li>`).join('')}</ul>`
        : '<p>No offices found.</p>';
      
      panelBody.innerHTML = `
        <p>${data.description}</p>
        ${officesHtml}
      `;
      panel.classList.add('active');
      mapContainer.classList.add('panel-open'); // Shift map
    } catch (err) {
      console.error('Error:', err);
      panelTitle.textContent = 'Building Info';
      panelBody.innerHTML = `<p>No data found for <strong>${id}</strong>.</p>`;
      panel.classList.add('active');
      mapContainer.classList.add('panel-open');
    }
  });
});

closeBtn.addEventListener('click', () => {
  panel.classList.remove('active');
  mapContainer.classList.remove('panel-open'); // Reset map position
  if (selectedElement) {
    selectedElement.style.filter = '';
    selectedElement = null;
  }
});
</script>
@endpush